package com.example.hogotest.glide

import android.os.Bundle
import android.widget.ImageView
import com.bumptech.glide.Glide
import com.hugo.mylibrary.components.BaseActivity
val base64String = "data:image/png;base64,"
class GlideTestActivity :BaseActivity(){
    lateinit var imageView :ImageView
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        imageView= ImageView(this)

        setContentView(imageView)
        Glide.with(imageView).load(base64String).into(imageView)

    }


}